mapper.insert:
insert into TB_SMB_SALES (
            work_id
           ,cmpy_id
           ,brand_id
           ,amt_year
           ,amt_month
           ,amt_term
           ,sales_money
           ,amt_money
           ,amt_kakao_money
           ,net_sales_money
           ,refund_money
           ,target_count
           ,perform_count
           ,reg_id
           ,reg_date
           ,reg_time
) values (
            #{work_id}
           ,#{cmpy_id}
           ,#{brand_id}
           ,#{amt_year}
           ,#{amt_month}
           ,#{amt_term}
           ,#{sales_money}
           ,#{amt_money}
           ,#{amt_kakao_money}
           ,#{net_sales_money}
           ,#{refund_money}
           ,#{target_count}
           ,#{perform_count}
           ,'SYSTEM'
           ,replace(convert(varchar(10), getDate(), 120), '-', '')
           ,replace(convert(varchar(8), getDate(), 108), ':', '')
)

mapper.update:
update TB_SMB_SALES set
       work_id = #{work_id}
      ,cmpy_id = #{cmpy_id}
      ,brand_id = #{brand_id}
      ,amt_year = #{amt_year}
      ,amt_month = #{amt_month}
      ,amt_term = #{amt_term}
      ,sales_money = #{sales_money}
      ,amt_money = #{amt_money}
      ,amt_kakao_money = #{amt_kakao_money}
      ,net_sales_money = #{net_sales_money}
      ,refund_money = #{refund_money}
      ,target_count = #{target_count}
      ,perform_count = #{perform_count}
      ,upd_id = 'SYSTEM'
      ,upd_date = replace(convert(varchar(10), getDate(), 120), '-', '')
      ,upd_time = replace(convert(varchar(8), getDate(), 108), ':', '')
 where idx = #{idx}
